\begin{tikzpicture}[circuit logic US,
    font=\small,
    % Gate styles
    or gate/.append style={draw, fill=white},
    not gate/.append style={draw, fill=white},
    and gate/.append style={draw, fill=white},
    % Wire style
    line width=0.8pt,
    % Input/Output connections style
    logic external connectors, % This enables .input and .output anchors
    every node/.style={minimum size=0.5cm} % Adjust size for better fit
]
    % Gates
    \node[or gate] (OR1) at (2, 0.5) {};
    \node[not gate] (NOT1) at (2, -1.5) {};
    \node[and gate] (AND1) at (4, -0.5) {};

    % Inputs
    \coordinate (x_start) at (0, 1.5);
    \coordinate (y_start) at (0, -0.5);

    \node[left=0.1cm of x_start] {x};
    \node[left=0.1cm of y_start] {y};

    % Connect x to OR gate
    \draw (x_start) -- (OR1.input 1);

    % Connect y to OR gate and NOT gate
    % Use a branch point for the 'y' input
    \draw (y_start) -- ([xshift=0.5cm]y_start) node[coordinate, midway] (y_branch) {} -- (OR1.input 2);
    \draw (y_branch) |- (NOT1.input);

    % Connect OR gate output to AND gate
    \draw (OR1.output) -- (AND1.input 1);

    % Connect NOT gate output to AND gate
    \draw (NOT1.output) -- (AND1.input 2);

    % Final Output
    \draw (AND1.output) -- node[above] {Output} ++(1.5, 0);
\end{tikzpicture}

\begin{tikzpicture}[
    % Set styles for gates, inputs, and outputs
    gate/.style={draw,logic gate,minimum width=0.8cm,minimum height=0.8cm, rounded corners=2pt},
    input_node/.style={coordinate},
    output_node/.style={coordinate},
    every path/.style={thick} % Thicker lines for visibility
]
    % Input wires on the left
    \node[input_node] (x0_coord) at (0,4.5) {}; \node[left of=x0_coord, anchor=east] {x0};
    \node[input_node] (x1_coord) at (0,3.5) {}; \node[left of=x1_coord, anchor=east] {x1};
    \node[input_node] (y0_coord) at (0,2.5) {}; \node[left of=y0_coord, anchor=east] {y0};
    \node[input_node] (y1_coord) at (0,1.5) {}; \node[left of=y1_coord, anchor=east] {y1};

    % --- First Stage: Partial Products (AND gates) ---
    % x0 * y0 -> P0
    \node[and gate,gate,anchor=west] (and_x0y0) at (2.5,3.75) {};
    \draw (x0_coord) -- ++(0.5,0) |- (and_x0y0.input 1);
    \draw (y0_coord) -- ++(0.5,0) |- (and_x0y0.input 2);
    \node[output_node] (p0_output) at ([xshift=2cm]and_x0y0.output) {};
    \draw (and_x0y0.output) -- (p0_output) node[right] {p0};

    % x1 * y0 (for P1, C1)
    \node[and gate,gate,anchor=west] (and_x1y0) at (2.5,2.75) {};
    \draw (x1_coord) -- ++(0.5,0) |- (and_x1y0.input 1);
    \draw (y0_coord) -- ++(0.5,0) |- (and_x1y0.input 2); % Tap y0

    % x0 * y1 (for P1, C1)
    \node[and gate,gate,anchor=west] (and_x0y1) at (2.5,1.75) {};
    \draw (x0_coord) -- ++(0.5,0) |- (and_x0y1.input 1); % Tap x0
    \draw (y1_coord) -- ++(0.5,0) |- (and_x0y1.input 2);

    % x1 * y1 (for P2, P3)
    \node[and gate,gate,anchor=west] (and_x1y1) at (2.5,0.75) {};
    \draw (x1_coord) -- ++(0.5,0) |- (and_x1y1.input 1); % Tap x1
    \draw (y1_coord) -- ++(0.5,0) |- (and_x1y1.input 2);

    % --- Second Stage: First Half-Adder equivalent (computes P1 and C1) ---
    % P1 = (x1y0 XOR x0y1)
    \node[xor gate,gate,anchor=west] (xor_p1) at (5.5,2.25) {};
    \draw (and_x1y0.output) -- ++(0.5,0) |- (xor_p1.input 1);
    \draw (and_x0y1.output) -- ++(0.5,0) |- (xor_p1.input 2);
    \node[output_node] (p1_output) at ([xshift=2cm]xor_p1.output) {};
    \draw (xor_p1.output) -- (p1_output) node[right] {p1};

    % C1 = (x1y0 AND x0y1) (Carry to next stage)
    \node[and gate,gate,anchor=west] (and_c1) at (5.5,1.25) {};
    \draw (and_x1y0.output) -- ++(0.5,0) |- (and_c1.input 1); % Tap x1y0 output
    \draw (and_x0y1.output) -- ++(0.5,0) |- (and_c1.input 2); % Tap x0y1 output

    % --- Third Stage: Second Half-Adder equivalent (computes P2 and P3) ---
    % P2 = (x1y1 XOR C1)
    \node[xor gate,gate,anchor=west] (xor_p2) at (8.5,1.25) {};
    \draw (and_x1y1.output) -- ++(0.5,0) |- (xor_p2.input 1);
    \draw (and_c1.output) -- ++(0.5,0) |- (xor_p2.input 2);
    \node[output_node] (p2_output) at ([xshift=2cm]xor_p2.output) {};
    \draw (xor_p2.output) -- (p2_output) node[right] {p2};

    % P3 = (x1y1 AND C1) (Carry from this stage)
    \node[and gate,gate,anchor=west] (and_p3) at (8.5,0.25) {};
    \draw (and_x1y1.output) -- ++(0.5,0) |- (and_p3.input 1); % Tap x1y1 output
    \draw (and_c1.output) -- ++(0.5,0) |- (and_p3.input 2); % Tap C1 output
    \node[output_node] (p3_output) at ([xshift=2cm]and_p3.output) {};
    \draw (and_p3.output) -- (p3_output) node[right] {p3};

\end{tikzpicture}

\begin{tikzpicture}[
    shorten >=1pt,
    node distance=2.5cm,
    on grid,
    auto,
    state/.style={circle, draw, minimum size=0.8cm}
  ]

  \node[state, initial, initial text=Start] (q0) {$q_0$};
  \node[state, accepting] (q1) [right of=q0] {$q_1$};
  \node[state] (q2) [right of=q1] {$q_2$};

  \path[->]
    (q0) edge node {$a$} (q1)
    (q0) edge [loop above] node {$b$} (q0)
    (q1) edge node {$b$} (q2)
    (q1) edge [loop above] node {$a$} (q1)
    (q2) edge [loop above] node {$a, b$} (q2);

\end{tikzpicture}

\begin{tikzpicture}[circuit logic US, thick]
    % NAND Gate
    \node[nand gate, draw, logic external inputs, logic external outputs] at (0,0) (nand_gate) {};
    \draw (nand_gate.input 1) -- ++(-0.5,0) node[left] {x};
    \draw (nand_gate.input 2) -- ++(-0.5,0) node[left] {y};
    \draw (nand_gate.output) -- ++(0.5,0) node[right] {x NAND y};

    % NOR Gate (positioned to the right of the NAND gate)
    \node[nor gate, draw, logic external inputs, logic external outputs] at (4,0) (nor_gate) {};
    \draw (nor_gate.input 1) -- ++(-0.5,0) node[left] {x};
    \draw (nor_gate.input 2) -- ++(-0.5,0) node[left] {y};
    \draw (nor_gate.output) -- ++(0.5,0) node[right] {x NOR y};

    % Add labels for the gates explicitly for clarity
    \node at (0, -1.5) {NAND Gate};
    \node at (4, -1.5) {NOR Gate};
\end{tikzpicture}

