    \node[kmap_header] {$x$}; & \node[kmap_cell] {1}; & \node[kmap_cell] {1}; \\
    \node[kmap_header] {$\bar{x}$}; & \node[kmap_cell] {1}; & \node[kmap_cell] {1}; \\
};
% Draw line for variable labels
\draw[thick] (topleft2.south east) -- ($(topleft2.south east)+(0.5,-0.5)$);
\node[above right, font=\sffamily\bfseries] at (topleft2.south east) {$y$};
\node[below left, font=\sffamily\bfseries] at (topleft2.south east) {$x$};

\end{tikzpicture}
